import React from "react";
import "./NavBar.css";
import useDropdownMenu from "react-accessible-dropdown-menu-hook";

function navBar() {
  const { buttonProps, itemProps, isOpen, setIsOpen } =
    useDropdownMenu(numberOfItems);

  return (
    <div className="navbar">
      <img
        className="logo"
        src="https://upload.wikimedia.org/wikipedia/commons/thumb/0/08/Netflix_2015_logo.svg/1920px-Netflix_2015_logo.svg.png"
        alt="Netflix"
      />
      <img
        className="avatar"
        src="https://www.yorkoptometryclinic.ca/images/img_avatar.png"
        alt=""
      />
      <button {...buttonProps}>Example</button>
      <div className={isOpen ? "visible" : ""} role="menu">
        <a {...itemProps[0]} href="https://example.com">
          Regular link
        </a>
        <a {...itemProps[1]} onClick={handleClick}>
          With click handler
        </a>
      </div>
    </div>
  );
}

export default navBar;
